(602, 104, '_elementor_data', '[{\"id\":\"59e9585\",\"elType\":\"section\",\"settings\":{\"background_background\":\"classic\",\"background_image\":{\"id\":55,\"url\":\"http:\\/\\/localhost\\/newhope\\/wp-content\\/uploads\\/2020\\/03\\/Soup-kitchen-3.jpeg\"},\"background_attachment\":\"fixed\",\"background_size\":\"cover\",\"padding\":{\"unit\":\"px\",\"top\":\"300\",\"right\":\"0\",\"bottom\":\"100\",\"left\":\"0\",\"isLinked\":false}},\"elements\":[{\"id\":\"3adb1309\",\"elType\":\"column\",\"settings\":{\"_column_size\":100},\"elements\":[{\"id\":\"df1ee6b\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<p>OUR STORY<\\/p>\",\"align\":\"left\",\"text_color\":\"#ffffff\",\"typography_typography\":\"custom\",\"typography_font_size\":{\"unit\":\"px\",\"size\":20},\"typography_font_family\":\"Open Sans\",\"_padding\":{\"unit\":\"px\",\"top\":\"0\",\"right\":\"0\",\"bottom\":\"0\",\"left\":\"0\",\"isLinked\":false}},\"elements\":[],\"widgetType\":\"text-editor\"},{\"id\":\"3299a948\",\"elType\":\"widget\",\"settings\":{\"title\":\"About Us\",\"title_color\":\"#ffffff\",\"typography_typography\":\"custom\",\"typography_font_size\":{\"unit\":\"px\",\"size\":71}},\"elements\":[],\"widgetType\":\"heading\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"2019330c\",\"elType\":\"section\",\"settings\":{\"margin\":{\"unit\":\"px\",\"top\":\"0\",\"right\":0,\"bottom\":\"0\",\"left\":0,\"isLinked\":false},\"padding\":{\"unit\":\"px\",\"top\":\"100\",\"right\":\"0\",\"bottom\":\"0\",\"left\":\"0\",\"isLinked\":false}},\"elements\":[{\"id\":\"549b146f\",\"elType\":\"column\",\"settings\":{\"_column_size\":100},\"elements\":[{\"id\":\"22ad4426\",\"elType\":\"widget\",\"settings\":{\"title\":\"A place to breathe.\",\"align\":\"center\",\"title_color\":\"#424242\",\"typography_typography\":\"custom\",\"typography_font_size\":{\"unit\":\"px\",\"size\":26},\"typography_font_family\":\"Open Sans\",\"typography_font_weight\":\"400\"},\"elements\":[],\"widgetType\":\"heading\"},{\